import { useState } from "react";
import shortid from "shortid";

const GradientBackground = () => {
  const [gradient, setGradient] = useState(["red", "blue", "yellow"]);
  const style = {
    // background: `linear-gradient(217deg, rgba(255,0,0,.8), rgba(255,0,0,0) 70.71%),
    //         linear-gradient(127deg, rgba(0,255,0,.8), rgba(0,255,0,0) 70.71%),
    //         linear-gradient(336deg, rgba(0,0,255,.8), rgba(0,0,255,0) 70.71%)`,
    background: `linear-gradient(217deg, ${gradient.join(",")})`,
    backgroundSize: "1200% 1200%",
    height: 900,
  };
  const getRandomKey = () => {
    return shortid.generate();
}
  const changeGradient = (colors) => {
    setGradient(colors)
  }
  return (
    <div
    key={getRandomKey()}
      className="bg-animation min-vh-100 d-flex align-content-center align-items-center flex-wrap m-0"
      style={style}
    >
      balls
      <button onClick={() => setGradient(["orange", "brown", "black"])}>Click me</button>
    </div>
  );
};

export default GradientBackground;
